import React from 'react';
import styled from 'styled-components';
import { FaArrowRight } from 'react-icons/fa'; // Иконка из react-icons
import { Carousel } from './components/Carousel'; // Компонент слайдера

// Глобальная стилизация
const AppContainer = styled.div`
  font-family: 'Roboto', sans-serif;
  color: #333;
  background: #f9f9f9;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 3em;
  font-weight: 700;
  color: #444;
  text-align: center;
  margin-bottom: 1em;
`;

const Description = styled.p`
  font-size: 1.2em;
  line-height: 1.6;
  color: #666;
  text-align: center;
  max-width: 800px;
  margin-bottom: 2em;
`;

const Button = styled.a`
  display: inline-block;
  padding: 12px 24px;
  background: linear-gradient(135deg, #007BFF, #0056D2);
  color: #fff;
  text-decoration: none;
  border-radius: 25px;
  font-size: 1.1em;
  transition: background 0.3s ease-in-out;
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
  display: flex;
  align-items: center;
  gap: 10px;

  &:hover {
    background: linear-gradient(135deg, #0056D2, #007BFF);
    box-shadow: 0 15px 20px rgba(0, 0, 0, 0.2);
  }
`;

function App() {
  return (
    <AppContainer>
      <Title>Welcome to the "TechnoSystems" Website!</Title>
      <Description>
        We provide innovative solutions for business automation. Discover how our technologies can help your business operate more efficiently!
      </Description>
      <Carousel />
      <Button href="/services">
        Explore Our Services <FaArrowRight />
      </Button>
    </AppContainer>
  );
}

export default App;
