import React, { useEffect, useRef } from 'react';
import { Box, Typography } from '@mui/material';

const ContactPage = () => {
  const mapContainerRef = useRef(null); // Контейнер для карты
  const mapInstanceRef = useRef(null); // Экземпляр карты

  useEffect(() => {
    const loadYandexMap = () => {
      // Проверка, загружен ли уже скрипт карты
      if (!document.querySelector('script[src="https://api-maps.yandex.ru/2.1/?apikey=a1fa1bec-ffeb-4909-a156-805619c69551&lang=ru_RU"]')) {
        const script = document.createElement('script');
        script.src = `https://api-maps.yandex.ru/2.1/?apikey=a1fa1bec-ffeb-4909-a156-805619c69551&lang=ru_RU`;
        script.type = 'text/javascript';
        script.onload = () => {
          window.ymaps.ready(initMap);
        };
        document.head.appendChild(script);
      } else {
        // Если скрипт уже загружен, сразу инициализируем карту
        window.ymaps.ready(initMap);
      }
    };

    const initMap = () => {
      if (mapContainerRef.current && !mapInstanceRef.current) {
        // Создаем экземпляр карты, если он еще не был создан
        mapInstanceRef.current = new window.ymaps.Map(mapContainerRef.current, {
          center: [53.388935, 83.745505], // Координаты для Барнаула
          zoom: 15,
        });

        // Добавляем метку на карту
        const myPlacemark = new window.ymaps.Placemark(
          [53.388935, 83.745505],
          {
            balloonContent: `
              <strong>ТехноСистемы</strong><br/>
              Телефон: +7 (495) 123-45-67<br/>
              Электронная почта: info@technosystems.com<br/>
              Адрес: г. Барнаул, пр-кт Калинина, д. 116/44
            `,
          },
          {
            preset: 'islands#icon',
            iconColor: '#DC143C',
          }
        );

        // Добавляем метку на карту
        mapInstanceRef.current.geoObjects.add(myPlacemark);
      }
    };

    loadYandexMap();
  }, []);

  return (
    <Box sx={{ padding: 4 }}>
      <Typography variant="h4" gutterBottom>
        Контакты
      </Typography>
      <Typography variant="body1" paragraph>
        Мы всегда рады помочь. Свяжитесь с нами!
      </Typography>
      <p><strong>Телефон:</strong> +7 (495) 123-45-67</p>
      <p><strong>Электронная почта:</strong> info@technosystems.com</p>
      <p><strong>Адрес:</strong> г. Барнаул, пр-кт Калинина, д. 116/44</p>
      <p><strong>График работы:</strong></p>
      <ul>
        <li>Пн - Пт: 09:00 - 18:00</li>
        <li>Сб: 10:00 - 15:00</li>
        <li>Вс: Выходной</li>
      </ul>
      {/* Карта Yandex */}
      <div ref={mapContainerRef} style={{ height: '400px', marginTop: '20px' }}></div>
    </Box>
  );
};

export default ContactPage;
