import React from 'react';
import Slider from 'react-slick';
import { Box } from '@mui/material';

const ImageSlider = () => {
  const settings = {
    dots: true, // Показывать точки навигации
    infinite: true, // Бесконечный цикл
    speed: 500, // Скорость анимации
    slidesToShow: 1, // Показывать один слайд за раз
    slidesToScroll: 1, // Прокручивать один слайд за раз
    autoplay: true, // Автопроигрывание
    autoplaySpeed: 3000, // Интервал автопроигрывания
  };

  return (
    <Box sx={{ maxWidth: '800px', margin: 'auto', padding: 4 }}>
      <Slider {...settings}>
        <div>
          <img src="/images/1.png" alt="Slide 1" style={{ width: '100%', borderRadius: '8px' }} />
        </div>
        <div>
          <img src="/images/1.png" alt="Slide 2" style={{ width: '100%', borderRadius: '8px' }} />
        </div>
        <div>
          <img src="https://via.placeholder.com/800x400?text=Картинка+3" alt="Slide 3" style={{ width: '100%', borderRadius: '8px' }} />
        </div>
      </Slider>
    </Box>
  );
};

export default ImageSlider;
