import React, { useState } from 'react';
import axios from 'axios';

const services = [
  { id: 1, name: 'Разработка ПО для кассовых аппаратов', price: '10000 руб.' },
  { id: 2, name: 'Интеграция с терминалами', price: '8000 руб.' },
  { id: 3, name: 'Обслуживание оборудования', price: '5000 руб.' },
  // Добавьте другие услуги
];

const ServicesPage = () => {
  const [selectedService, setSelectedService] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    service: '',
    message: ''
  });
  const [orderSubmitted, setOrderSubmitted] = useState(false);

  const handleOrderClick = (service) => {
    setSelectedService(service);
    setFormData({ ...formData, service: service.name });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Отправка данных на сервер
    axios.post('http://localhost:5000/api/orders', formData)
      .then(response => {
        setOrderSubmitted(true);
        setTimeout(() => {
          window.location.href = '/';  // Перенаправление на главную страницу через 5 секунд
        }, 5000);
      })
      .catch(error => {
        console.error('Ошибка при отправке данных:', error);
      });
  };

  return (
    <div>
      <h1>Услуги</h1>
      <table>
        <thead>
          <tr>
            <th>Название услуги</th>
            <th>Цена</th>
            <th>Действие</th>
          </tr>
        </thead>
        <tbody>
          {services.map((service) => (
            <tr key={service.id}>
              <td>{service.name}</td>
              <td>{service.price}</td>
              <td>
                <button onClick={() => handleOrderClick(service)}>Заказать</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedService && !orderSubmitted && (
        <div>
          <h2>Форма заказа: {selectedService.name}</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Имя</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label>Услуга</label>
              <input
                type="text"
                name="service"
                value={formData.service}
                readOnly
              />
            </div>
            <div>
              <label>Сообщение</label>
              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit">Заказать</button>
          </form>
        </div>
      )}

      {orderSubmitted && (
        <div>
          <h3>Ваш заказ принят! Мы свяжемся с вами в ближайшее время.</h3>
        </div>
      )}
    </div>
  );
};

export default ServicesPage;
