import React from 'react';
import './HomePage.css'; // Файл для стилизации страницы

const HomePage = () => {
  return (
    <div className="home-page">
      <h1>Добро пожаловать на сайт "ТехноСистемы"!</h1>
      <p>Мы предлагаем инновационные решения в сфере автоматизации бизнеса. Узнайте, как наши технологии могут помочь вашему бизнесу работать эффективнее!</p>

      {/* Слайдер */}
      <div className="slider">
        <div><img src="/images/1.png" alt="Slide 1" /></div>
        <div><img src="/images/2.png" alt="Slide 2" /></div>
        <div><img src="/images/3.png" alt="Slide 3" /></div>
      </div>

      <a href="/services" className="button">Наши услуги</a>
    </div>
  );
};

export default HomePage;
