import React, { useState, useEffect } from 'react';
import './HomePage.css'; // Файл для стилизации страницы

const HomePage = () => {
  const images = [
    '/images/1.png',
    '/images/2.png',
    '/images/3.png',
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Функция для перехода к следующему слайду
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Функция для перехода к предыдущему слайду
  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // Используем useEffect для автопрокрутки
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000); // Прокручивать слайдер каждые 3 секунды

    // Очистить интервал при размонтировании компонента
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-page">
      <h1>Добро пожаловать на сайт "ТехноСистемы"!</h1>
      <p>Мы предлагаем инновационные решения в сфере автоматизации бизнеса. Узнайте, как наши технологии могут помочь вашему бизнесу работать эффективнее!</p>

      {/* Слайдер */}
      <div className="slider">
        <div className="slides">
          <img src={images[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
        </div>

        {/* Кнопки для ручного управления */}
        <button className="prev" onClick={prevSlide}>❮</button>
        <button className="next" onClick={nextSlide}>❯</button>
      </div>

      <a href="/services" className="button">Наши услуги</a>
    </div>
  );
};

export default HomePage;
