import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { Link } from 'react-router-dom'; // Для переходов между страницами

const Header = () => {
  return (
    <AppBar position="sticky" sx={{ backgroundColor: '#1A237E', height: '80px' }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        {/* Логотип и название компании */}
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <img 
            src="/images/4.png" 
            alt="Logo" 
            style={{ width: '40px', marginRight: '10px', borderRadius: '50%' }} 
          />
          <Typography variant="h4" sx={{ fontFamily: 'Caveat',  fontWeight: '1000', color: '#fff', letterSpacing: '2px', fontSize: '30px', textTransform: 'uppercase' }}>
            ТехноСистемы
          </Typography>
        </Box>

        {/* Кнопки для перехода по страницам */}
        <Box sx={{ display: 'flex', gap: '20px' }}>
          <Button 
            component={Link} 
            to="/about" 
            variant="text" 
            sx={{ color: '#fff', '&:hover': { backgroundColor: '#303F9F' } }}
          >
            О нас
          </Button>
          <Button 
            component={Link} 
            to="/services" 
            variant="text" 
            sx={{ color: '#fff', '&:hover': { backgroundColor: '#303F9F' } }}
          >
            Услуги
          </Button>
          <Button 
            component={Link} 
            to="/contact" 
            variant="text" 
            sx={{ color: '#fff', '&:hover': { backgroundColor: '#303F9F' } }}
          >
            Контакты
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
