import React, { useEffect } from 'react';
import { Box, Typography } from '@mui/material';

const ContactPage = () => {
  useEffect(() => {
    // Проверяем, загружен ли скрипт
    const yandexScript = document.querySelector('script[src="https://api-maps.yandex.ru/2.1/?apikey=a1fa1bec-ffeb-4909-a156-805619c69551&lang=ru_RU"]');
    if (!yandexScript) {
      const script = document.createElement('script');
      script.src = `https://api-maps.yandex.ru/2.1/?apikey=YOUR_API_KEY&lang=ru_RU`;
      script.type = 'text/javascript';
      script.onload = () => {
        window.ymaps.ready(initMap);
      };
      document.head.appendChild(script);
    } else {
      // Если скрипт уже загружен, просто инициализируем карту
      window.ymaps.ready(initMap);
    }
  }, []);

  const initMap = () => {
    const mapContainer = document.getElementById('map');
    // Удаляем предыдущую карту, если она существует, чтобы избежать дублирования
    if (mapContainer.hasChildNodes()) {
      mapContainer.innerHTML = '';
    }

    const myMap = new window.ymaps.Map(mapContainer, {
      center: [53.3477, 83.7794], // Координаты для Барнаула
      zoom: 15,
    });

    const myPlacemark = new window.ymaps.Placemark([53.3477, 83.7794], {
      balloonContent: 'ТехноСистемы',
    });

    myMap.geoObjects.add(myPlacemark);
  };

  return (
    <Box sx={{ padding: 4 }}>
      <Typography variant="h4" gutterBottom>
        Контакты
      </Typography>
      <Typography variant="body1" paragraph>
        Мы всегда рады помочь. Свяжитесь с нами!
      </Typography>
      <p><strong>Телефон:</strong> +7 (495) 123-45-67</p>
      <p><strong>Электронная почта:</strong> info@technosystems.com</p>
      <p><strong>Адрес:</strong> г. Барнаул, пр-кт Калинина, д. 116/44</p>
      <p><strong>График работы:</strong></p>
      <ul>
        <li>Пн - Пт: 09:00 - 18:00</li>
        <li>Сб: 10:00 - 15:00</li>
        <li>Вс: Выходной</li>
      </ul>
      {/* Карта Yandex */}
      <div id="map" style={{ height: '400px', marginTop: '20px' }}></div>
    </Box>
  );
};

export default ContactPage;
